import React, { useState } from "react";
import {
    Accordion,
    AccordionItem,
    AccordionButton,
    AccordionPanel,
    AccordionIcon,
    Box,
    FormLabel,
    Switch,
    Button,
    Collapse,
    NumberInput,
    NumberInputField,
    NumberInputStepper,
    NumberIncrementStepper,
    NumberDecrementStepper,
    Slider,
    SliderThumb,
    SliderTrack,
    SliderFilledTrack,
    Tabs,
    TabList,
    Tab,
    TabPanel,
    TabPanels,
} from "@chakra-ui/react";
import Fade from "react-reveal/Fade";
import { PricingCard, PricingQuote } from "../../components/cards/PricingCard";
import ucaas from "../pages/solutions/ucaas";
import RadioGroup from "../../components/inputs/RadioGroup";


const verifyNotEmpty = (x, text) => (x > 0 ? ", " + x + " " + text : "");

const CustomRadio = React.forwardRef((props, ref) => {
    const { isChecked, isDisabled, value, ...rest } = props;
    return (
        <Button
            className="my-1"
            fontSize={["12px", "14px", "15px"]}
            ref={ref}
            variant={isChecked ? "solid" : "outline"}
            colorScheme={isChecked ? "primary" : "blue"}
            aria-checked={isChecked}
            role="radio"
            isDisabled={isDisabled}
            {...rest}
        />
    );
});

function UcaasPricing(props) {
    const [node, setNode] = useState("");
    const [type, setType] = useState("20-10 SIP");
    const [additionalUcaas, setadditionalUcaas] = useState(0);
    const [hunts, setHunts] = useState(0);
    const [addPhones, setAddPhones] = useState(0);
    const [num10, setNum10] = useState(0);
    const [num50, setNum50] = useState(0);
    const [num100, setNum100] = useState(0);
    const [extraVM, setExtraVM] = useState(0);
    const [show, setShow] = React.useState(false);
    const openControls = () => { setShow(true); window.scrollTo(0, document.getElementById("collapse-1").offsetTop - 100); }
    const [value, setValue] = React.useState(0);
    const handleChange = (value) => {
        setValue(value);
        setExtraVM(value);
    };
    const [showSIP, setShowSIP] = useState(false);
    const [addSIP, setAddSIP] = useState(0);
    const [advancedOptions, setAdvancedOptions] = useState(false);


    const [dx650, setdx650] = useState(0);
    const [dx80, setdx80] = useState(0);
    const [dx70, setdx70] = useState(0);
    const [p9951, setp9951] = useState(0);
    const [p8845, setp8845] = useState(0);
    const [p7965g, setp7965g] = useState(0);
    var totalPhones = parseInt(type == "100-50 SIP" ? type.slice(0, 3) : type.slice(0, 2)) + additionalUcaas + addPhones;
    const [confSwitch, setconfSwitch] = React.useState(false);
    const handleSwitch = (swi) => swi ? ",Add Conference Phone" : "";

    const [aj179, setaj179] = useState(0);
    const [a1408, seta1408] = useState(0);
    const [a9608, seta9608] = useState(0);

    const [fanvilc400, setfanvilc400] = useState(0);
    const [fanvilc600, setfanvilc600] = useState(0);

    const resetOnCardChange = () => {
        setNum10(0);
        setNum50(0);
        setNum100(0);
        setAddSIP(0);
        setValue(0);
        setExtraVM(0);
        setadditionalUcaas(0);
        setAddPhones(0);
        setHunts(0);
        resetPhones();
        setType("20-10 SIP");

    };

    const resetPhones = () => {
        setdx650(0);
        setdx80(0);
        setdx70(0);
        setp9951(0);
        setp8845(0);
        setp7965g(0);
        setaj179(0);
        seta1408(0);
        seta9608(0);
        setfanvilc400(0);
        setfanvilc600(0);
        setconfSwitch(false);
    };
    return (

        <div id="pricing" className="container">


            <Tabs onChange={() => { setShow(false); resetPhones(); }} align="center">
                <TabList
                >
                    <Tab className="py-4 px-lg-5 display6">
                        Cisco
                    </Tab>
                    <Tab className="py-4 px-lg-5 display6">
                        Avaya IP Office
                    </Tab>
                    <Tab className="py-4 px-lg-5 display6">
                        3CX
                    </Tab>
                </TabList>

                <TabPanels>
                    <TabPanel className="py-2">
                        <Fade
                            duration={500}
                            distance={"30%"}
                            bottom
                        >
                            <div className="row no-gutters justify-content-center">
                                <div className="col-lg-4 my-3">
                                    <PricingCard
                                        title="Single Node CUCM"
                                        icon="/assets/images/icons/theme/fax.svg"
                                        featureList={[
                                            "Include Voicemail",
                                            "Include Presence",
                                            "Include Voicemail to Email*",
                                            "Include Cisco Jabber",
                                        ]}
                                    >
                                        <a href="#collapse-1" className="no-red">
                                            <Button
                                                className="mt-3"
                                                colorScheme="primary"
                                                variant="outline"
                                                size="lg"
                                                onClick={() => {
                                                    openControls();
                                                    setNode(
                                                        "Cisco / Single Node CUCM"
                                                    );
                                                    resetOnCardChange();
                                                }}
                                            >
                                                View More
                                            </Button>
                                        </a>
                                    </PricingCard>
                                </div>
                                <div className="col-lg-4 my-3">
                                    <PricingCard
                                        title="Dual Node CUCM"
                                        icon="/assets/images/icons/theme/fax.svg"
                                        featureList={[
                                            "Include Voicemail",
                                            "Include Presence",
                                            "Include Voicemail to Email*",
                                            "Include Cisco Jabber",
                                        ]}
                                    >
                                        <a href="#collapse-1" className="no-red">
                                            <Button
                                                className="mt-3"
                                                colorScheme="primary"
                                                variant="outline"
                                                size="lg"
                                                onClick={() => {
                                                    openControls();
                                                    setNode(
                                                        "Cisco / Dual Node CUCM"
                                                    );
                                                    resetOnCardChange();
                                                }}
                                            >
                                                View More
                                            </Button>
                                        </a>
                                    </PricingCard>
                                </div>
                                <div className="col-lg-4 my-3">
                                    <PricingCard
                                        className="hover-effect"
                                        title="Multiple Nodes CUCM"
                                        icon="/assets/images/icons/theme/fax.svg"
                                        featureList={[
                                            "Include Voicemail",
                                            "Include Presence",
                                            "Include Voicemail to Email*",
                                            "Include Cisco Jabber",
                                        ]}
                                    >
                                        <a href="#collapse-1" className="no-red">
                                            <Button
                                                className="mt-3"
                                                colorScheme="primary"
                                                variant="outline"
                                                size="lg"
                                                onClick={() => {
                                                    openControls();
                                                    setNode(
                                                        "Cisco / Multiple Nodes CUCM"
                                                    );
                                                    resetOnCardChange();
                                                }}
                                            >
                                                View More
                                            </Button>
                                        </a>
                                    </PricingCard>
                                </div>
                            </div>
                        </Fade>
                    </TabPanel>
                    <TabPanel className="py-2">
                        <Fade
                            duration={500}
                            distance={"30%"}
                            bottom
                        >
                            <div className="row no-gutters justify-content-center">
                                <div className="col-lg-4 my-3">
                                    <PricingCard
                                        title="Single Node"
                                        icon="/assets/images/icons/theme/firewall_virtual.svg"
                                        featureList={[
                                            "Include Voicemail",
                                            "Include Presence",
                                            "Include Voicemail to Email*",
                                            "Complete Mobility",
                                            "Cost-Effective",
                                            "Flexibly Deployed",
                                            "Highly Scalable",

                                        ]}
                                    >
                                        <a href="#collapse-1" className="no-red">
                                            <Button
                                                className="mt-3"
                                                colorScheme="primary"
                                                variant="outline"
                                                size="lg"
                                                onClick={() => {
                                                    openControls();
                                                    setNode(
                                                        "Avaya IP Office / Single Node"
                                                    );
                                                    resetOnCardChange();
                                                }}
                                            >
                                                View More
                                            </Button>
                                        </a>
                                    </PricingCard>
                                </div>
                                <div className="col-lg-4 my-3">
                                    <PricingCard
                                        title="Dual Node"
                                        icon="/assets/images/icons/theme/firewall_virtual.svg"
                                        featureList={[
                                            "Include Voicemail",
                                            "Include Presence",
                                            "Include Voicemail to Email*",
                                            "Complete Mobility",
                                            "Cost-Effective",
                                            "Flexibly Deployed",
