                                        "Cisco 7965G"
                                    )}
                                                    ${verifyNotEmpty(
                                        aj179,
                                        "Avaya AJ179"
                                    )}
                                                    ${verifyNotEmpty(
                                        a1408,
                                        "Avaya A1408"
                                    )}
                                                    ${verifyNotEmpty(
                                        a9608,
                                        "Avaya A9608"
                                    )}
                                                    ${verifyNotEmpty(
                                        fanvilc400,
                                        "3CX Fanvil C400"
                                    )}
                                                    ${verifyNotEmpty(
                                        fanvilc600,
                                        "3CX Fanvil C600"
                                    )}
                                                    ${handleSwitch(confSwitch)}
                                                    `}
                                ></PricingQuote>
                            </div>
                        </div>
                    </div>
                </Collapse>
            </div>
        </div>
    )
}

export default UcaasPricing;
