                                                                    setconfSwitch(e.target.checked);
                                                                    }}
                                                                    isChecked={confSwitch}
                                                                    id="license"
                                                                />
                                                                
                                                        </div>

                                                        </div>
                                                        
    </AccordionPanel> }

                                                        </AccordionItem>
</Accordion>
                                            </div>

                                            <div className="col-lg-12 mt-4 d-flex justify-content-center">
                                                <PricingQuote
                                                    button
                                                    serviceName="Unified Communications As A Service"
                                                    serviceDescription={`${node},${type}
                                                    ${verifyNotEmpty(
                                                        addSIP,
                                                        "Additional SIP"
                                                    )}
                                                    ${verifyNotEmpty(
                                                        additionalUcaas,
                                                        "Additional UCaaS"
                                                    )}
                                                    ${verifyNotEmpty(
                                                        addPhones,
                                                        "Additonal Phones"
                                                    )}${verifyNotEmpty(
                                                        hunts,
                                                        "Additonal Hunts"
                                                    )}
                                                    ${verifyNotEmpty(
                                                        extraVM,
                                                        "Extra VMs"
                                                    )}${verifyNotEmpty(
                                                        num10,
                                                        "NUM-10"
                                                    )}${verifyNotEmpty(
                                                        num50,
                                                        "NUM-50"
                                                    )}${verifyNotEmpty(
                                                        num100,
                                                        "NUM-100"
                                                    )}
                                                    
                                                    ${verifyNotEmpty(
                                                        dx650,
                                                        "Cisco DX650"
                                                    )}
                                                    ${verifyNotEmpty(
                                                        dx80,
                                                        "Cisco DX80"
                                                    )}
                                                    ${verifyNotEmpty(
                                                        dx70,
                                                        "Cisco DX70"
                                                    )}
                                                    ${verifyNotEmpty(
                                                        p9951,
                                                        "Cisco 9951"
                                                    )}
                                                    ${verifyNotEmpty(
                                                        p8845,
                                                        "Cisco 8845"
                                                    )}
                                                    ${verifyNotEmpty(
                                                        p7965g,
                                                        "Cisco 7965G"
                                                    )}
                                                    ${verifyNotEmpty(
                                                        aj179,
                                                        "Avaya AJ179"
                                                    )}
                                                    ${verifyNotEmpty(
                                                        a1408,
                                                        "Avaya A1408"
                                                    )}
                                                    ${verifyNotEmpty(
                                                        a9608,
                                                        "Avaya A9608"
                                                    )}
                                                    ${verifyNotEmpty(
                                                        fanvilc400,
                                                        "3CX Fanvil C400"
                                                    )}
                                                    ${verifyNotEmpty(
                                                        fanvilc600,
                                                        "3CX Fanvil C600"
                                                    )}
                                                    ${handleSwitch(confSwitch)}
                                                    `}
                                                ></PricingQuote>
                                            </div>
                                        </div>
                                    </div>
                                </Collapse>
                            </div>
                        
                    
                </div>
    )
}

export default UcaasPricing;
